string GetTitle(string content) {
	int start;
	int end;
	int total;
	string text;

	string str = "<h1>";
	start = content.find(str) + str.length();
	end = content.find("</h1>");
	total = end-start;
	text = content.substr(start, total);
	return text;
}

string GetClearText(string content) {
	int start;
	int end;
	int total;
	string text = content;

	while (true) {
		start = text.find('<');
		end = text.find('>')+1;
		if (start < 0) {
			break;
		}
		total = end-start;
		text.erase(start, total);
	}
	return text;
}

