string GetClearWord(string str) {
	int start;
	vector<wchar_t> arr = {'"', ',', '.', '!', '?', ':', ';', '(', ')', L'«', L'»'};
	wstring word = s2ws(str);

	for (wchar_t item : arr) {
		while (true) {
			start = word.find(item);
			if (start < 0) {
				break;
			}
			word.erase(start, 1);
		}
	}
	StringLower(word);

	return ws2s(word);
}

vector<string> GetWords(string text) {
	vector<string> output;
	vector<string> arr = Split(text);

	for (string word : arr) {
		if (word.length() >= 3) {
			word = GetClearWord(word);
			output.push_back(word);
		}
	}
	return output;
}

string GetLanguage(string text) {
	int i = 0;
	int ru = 0;
	int en = 0;
	int other = 0;
	int textlen = text.size();
	wstring wtext = s2ws(text);

	for (wchar_t item : wtext) {
		if (ru > textlen/2 || en > textlen/2 || other > textlen/2) {
			break;
		}

		if (item >= L'А' && item <= L'Я' || item >= L'а' && item <= L'я') {
			ru++;
		}
		else if (item >= 'A' && item <= 'Z' || item >= 'a' && item <= 'z') {
			en++;
		}
		else {
			other++;
		}
	}
	if (ru > en && ru > other) {
		return "ru";
	}
	if (en > ru && en > other) {
		return "en";
	}
	if (other > ru && other > en) {
		return "--";
	}
}

string as(string dirname) {
	int a = dirname.size()-1;
	char s = dirname[a];
	if (s != '/') {
		dirname.append("/");
	}
	return dirname;
}

vector<string> OpenDirectory(string dirname, string mod) {
	vector<string> output;

	const char *c_dirname = dirname.c_str();
	DIR *dir = opendir(c_dirname);
	if (dir) {
		struct dirent *ent;
		while ((ent = readdir(dir)) != NULL) {
			dirname = ent->d_name;
			if (dirname != "." && dirname != "..") {
				int a = dirname.find(".");
				if (a < 0 && mod == "dirs") {
					output.push_back(dirname);
				}
				else if (a > 0 && mod == "files") {
					output.push_back(dirname);
				}
			}
		}
		closedir(dir);
	}
	else {
		string error_msg = "Error opening directory: ";
		print(error_msg.append(dirname));
	}
	return output;
}

vector<string> ReadDirectory(string dirname) {
	vector<string> buff;
	vector<string> buff2;
	vector<string> output;

	buff = OpenDirectory(dirname, "files");
	for (string item : buff) {
		output.push_back(as(dirname).append(item));
	}

	buff = OpenDirectory(dirname, "dirs");
	for (string item : buff) {
		string dirname2 = as(dirname);
		dirname2 = dirname2.append(item);
		buff2 = ReadDirectory(dirname2);
		for (string item2 : buff2) {
			output.push_back(item2);
		}
	}
	return output;
}

