class Docs {
	public:
		int id;
		string fname;
		string content;
		string title;
		string text;
		string lang;
		bool isnews;
		string theme;
		vector<string> words;
		vector<int> groups;

		void calculate() {
			content = ReadFile(fname);
			title = GetTitle(content);
			text = GetClearText(content);
			lang = GetLanguage(text);
			if (lang != "ru" && lang != "en") {
				return;
			}

			words = GetWords(text);
			isnews = GetNews(lang, text, words);
			if (isnews == false) {
				return;
			}

			theme = GetThemes(lang, text, words);
		}
	private:
		float average_ball;
};

void AddDocsToArray(vector<Docs>& docs, string& fname) {
	Docs doc;
	doc.id = docs.size();
	doc.fname = fname;
	

	doc.calculate();
	docs.push_back(doc);
	print("--- --- ---");
	print(doc.id);
	print(doc.words.size());
}

void Group(vector<Docs>& docs, Docs& doc) {
	bool result;

	print("+++ +++ +++");
	print(doc.id);
	print(doc.words.size());

	for (Docs chdoc : docs) {
		result = IsItemInArray(doc.groups, chdoc.id);
		if (result) {
			continue;
		}

		for (string word : doc.words) {
			result = IsItemInArray(chdoc.words, word);
			if (result) {
				doc.groups.push_back(chdoc.id);
				//cout << doc.id << " +" << chdoc.id << endl;
			}
		}
	}
}

void Grouping() {
	clock_t start, stop;

	print("start initialization");
	start = clock();
	vector<Docs> docs;
	vector<string> filelist = ReadDirectory("source_dir");
	print(filelist.size());
	for (string fname : filelist) {
		AddDocsToArray(docs, fname);
	}
	stop = clock();
	printf("Loop required %f seconds\n", (double)(stop - start) / CLOCKS_PER_SEC);

	print("start grouping");
	start = clock();
	for (Docs doc : docs) {
		Group(docs, doc);
	}

	stop = clock();
	printf("Loop required %f seconds\n", (double)(stop - start) / CLOCKS_PER_SEC);
}

